/**
  * @brief  The application entry point.
  *
  * @retval None
  */
int main(void)
{
  /* USER CODE BEGIN 1 */
  char agentPolicy[] = "This is the agent policy.\r\n";
  char compoundIdStr[27] = {0};
  /* USER CODE END 1 */

  /* MCU Configuration----------------------------------------------------------*/

  /* Reset of all peripherals, Initializes the Flash interface and the Systick. */
  HAL_Init();

  /* USER CODE BEGIN Init */

  /* USER CODE END Init */

  /* Configure the system clock */
  SystemClock_Config();

  /* USER CODE BEGIN SysInit */

  /* USER CODE END SysInit */

  /* Initialize all configured peripherals */
  MX_GPIO_Init();
  MX_RNG_Init();
  MX_RTC_Init();
  MX_USART2_UART_Init();
  MX_USB_DEVICE_Init();
  /* USER CODE BEGIN 2 */
  InitializeITM();


  fprintf(stderr, "\r\n\r\n"
                  "================\r\n"
                  " Barnacle Agent\r\n"
                  "================\r\n");

  char* dateStr = asctime(localtime((time_t*)&AgentHdr.s.sign.agent.issued));
  dateStr[24] = '\0';
  printf("%s (%u bytes, V%d.%d, %s)\r\n",
         AgentHdr.s.sign.agent.name,
         (unsigned int)AgentHdr.s.sign.agent.size,
         (short unsigned int)AgentHdr.s.sign.agent.version >> 16,
         (short unsigned int)AgentHdr.s.sign.agent.version % 0x0000ffff,
         dateStr);
  BarnacleTAGetCompoundID((RIOT_ECC_PUBLIC*)&pCertStore->info.devicePubKey, compoundIdStr);
  logInfo("DeviceID          : %s\r\n", compoundIdStr);
  BarnacleTAGetCompoundID((RIOT_ECC_PUBLIC*)&pCompoundId->info.pubKey, compoundIdStr);
  logInfo("CompoundID[Code]  : %s\r\n", compoundIdStr);
//  {
//      uint8_t devicePub[65] = {0};
//      RiotCrypt_ExportEccPub((RIOT_ECC_PUBLIC*)&pCertStore->info.devicePubKey, devicePub, NULL);
//      for(uint32_t n = 0; n < sizeof(devicePub); n++)
//      {
//          if (!((n + 1) % 22) && (n > 0)) dbgPrintAppend("\r\n");
//          dbgPrintAppend("%02x", devicePub[n]);
//      }
//  }
//  dbgPrintAppend("\r\n");

  // Issue policy based end cert
  if(!BarnacleTADerivePolicyIdentity((uint8_t*)agentPolicy, sizeof(agentPolicy)))
  {
      logError("BarnacleDerivePolicyIdentity failed.\r\n");
      Error_Handler();
  }
  BarnacleTAGetCompoundID((RIOT_ECC_PUBLIC*)&pCompoundId->info.pubKey, compoundIdStr);
  logInfo("CompoundID[Policy]: %s\r\n\r\n", compoundIdStr);

  BarnacleTAPrintCertStore();

  /* USER CODE END 2 */

  /* Infinite loop */
  /* USER CODE BEGIN WHILE */
  while (1)
  {
      uint32_t trigger = HAL_RNG_GetRandomNumber(&hrng);

  /* USER CODE END WHILE */

  /* USER CODE BEGIN 3 */
      HAL_Delay(5000);
      printf("Hello World! (0x%08x)\r\n", (unsigned int)trigger);
      if((trigger & 0x0000000f) == 0x0f)
      {
          uint32_t* pAttack = (uint32_t*)0x080fe000;
          printf("Simulating Attack...\r\n");
          logInfo("Agent going down.\r\n");
          HAL_Delay(1);
          trigger = *pAttack;
      }
  }
  /* USER CODE END 3 */

}

/**
  * @brief System Clock Configuration
  * @retval None
  */
void SystemClock_Config(void)
{

  RCC_OscInitTypeDef RCC_OscInitStruct;
  RCC_ClkInitTypeDef RCC_ClkInitStruct;
  RCC_PeriphCLKInitTypeDef PeriphClkInit;

    /**Configure LSE Drive Capability 
    */
  HAL_PWR_EnableBkUpAccess();

  __HAL_RCC_LSEDRIVE_CONFIG(RCC_LSEDRIVE_LOW);

    /**Initializes the CPU, AHB and APB busses clocks 
    */
  RCC_OscInitStruct.OscillatorType = RCC_OSCILLATORTYPE_HSI48|RCC_OSCILLATORTYPE_HSI
                              |RCC_OSCILLATORTYPE_LSE;
  RCC_OscInitStruct.LSEState = RCC_LSE_ON;
  RCC_OscInitStruct.HSIState = RCC_HSI_ON;
  RCC_OscInitStruct.HSI48State = RCC_HSI48_ON;
  RCC_OscInitStruct.HSICalibrationValue = 64;
  RCC_OscInitStruct.PLL.PLLState = RCC_PLL_ON;
  RCC_OscInitStruct.PLL.PLLSource = RCC_PLLSOURCE_HSI;
  RCC_OscInitStruct.PLL.PLLM = 1;
  RCC_OscInitStruct.PLL.PLLN = 10;
  RCC_OscInitStruct.PLL.PLLP = RCC_PLLP_DIV2;
  RCC_OscInitStruct.PLL.PLLQ = RCC_PLLQ_DIV2;
  RCC_OscInitStruct.PLL.PLLR = RCC_PLLR_DIV2;
  if (HAL_RCC_OscConfig(&RCC_OscInitStruct) != HAL_OK)
  {
    _Error_Handler(__FILE__, __LINE__);
  }

    /**Initializes the CPU, AHB and APB busses clocks 
    */
  RCC_ClkInitStruct.ClockType = RCC_CLOCKTYPE_HCLK|RCC_CLOCKTYPE_SYSCLK
                              |RCC_CLOCKTYPE_PCLK1|RCC_CLOCKTYPE_PCLK2;
  RCC_ClkInitStruct.SYSCLKSource = RCC_SYSCLKSOURCE_PLLCLK;
  RCC_ClkInitStruct.AHBCLKDivider = RCC_SYSCLK_DIV1;
  RCC_ClkInitStruct.APB1CLKDivider = RCC_HCLK_DIV1;
  RCC_ClkInitStruct.APB2CLKDivider = RCC_HCLK_DIV1;

  if (HAL_RCC_ClockConfig(&RCC_ClkInitStruct, FLASH_LATENCY_4) != HAL_OK)
  {
    _Error_Handler(__FILE__, __LINE__);
  }

  PeriphClkInit.PeriphClockSelection = RCC_PERIPHCLK_RTC|RCC_PERIPHCLK_USART2
                              |RCC_PERIPHCLK_USB|RCC_PERIPHCLK_RNG;
  PeriphClkInit.Usart2ClockSelection = RCC_USART2CLKSOURCE_PCLK1;
  PeriphClkInit.RTCClockSelection = RCC_RTCCLKSOURCE_LSE;
  PeriphClkInit.UsbClockSelection = RCC_USBCLKSOURCE_HSI48;
  PeriphClkInit.RngClockSelection = RCC_RNGCLKSOURCE_HSI48;
  if (HAL_RCCEx_PeriphCLKConfig(&PeriphClkInit) != HAL_OK)
  {
    _Error_Handler(__FILE__, __LINE__);
  }

    /**Configure the main internal regulator output voltage 
    */
  if (HAL_PWREx_ControlVoltageScaling(PWR_REGULATOR_VOLTAGE_SCALE1) != HAL_OK)
  {
    _Error_Handler(__FILE__, __LINE__);
  }

    /**Configure the Systick interrupt time 
    */
  HAL_SYSTICK_Config(HAL_RCC_GetHCLKFreq()/1000);

    /**Configure the Systick 
    */
  HAL_SYSTICK_CLKSourceConfig(SYSTICK_CLKSOURCE_HCLK);

  /* SysTick_IRQn interrupt configuration */
  HAL_NVIC_SetPriority(SysTick_IRQn, 0, 0);
}

